import React, { Component } from "react"; // imrc
import Counter from "./counter";

class Counters extends Component {
  // cc
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 }
    ]
  };
  render() {
    return (
      <div>
        {this.state.counters.map(c => (
          <Counter key={c.id} value={c.value} selec />
        ))}
      </div>
    );
  }
} 

export default Counters;
