import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };

  styles = {
    fontSize: 10,
    fontWeight: "bold"
  };

  // constructor() {
  //   super();
  //   this.handleIncerment = this.handleIncerment.bind(this);
  // }

  renderTags() {
    if (this.state.count === 0) return <p>There are no tags!</p>;

    return (
      <ul>
        {this.state.tags.map(val => (
          <li>{val}</li>
        ))}
      </ul>
    );
  }

  handleIncerment = (product) => {
    console.
    this.setState({ count: this.state.count + 1 });
  };

  render() {
    return (
      <div>
        <span style={this.styles} className={this.getBadgeClasses()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() => this.handleIncerment}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
      </div>
    );
  }

  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { count } = this.state; // picking the count property of this.state
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
